'use client'

import React from 'react'
import { ScrollBar, Separator } from '@/registry/default/ui'
import { ScrollArea } from '@/registry/default/ui'
import { Button, buttonVariants } from '../button'
import { Clipboard, X } from 'lucide-react'
import {
  FileType,
  FolderType,
  SelectedFolderType,
  UploadAdvancedContextType,
  UploadAdvancedProviderProps,
} from './upload.types'
// import { UploadManager } from './upload.lib'
import {
  UploadAddFolderButton,
  UploadAdvancedButton,
  UploadAlertDeleteAttachments,
  UploadAlertMoveAction,
  UploadAttachmentsTreeItem,
  UploadDownloadAttachments,
  UploadReloadButton,
  UploadSearchButton,
  UploadViewButton,
} from './upload-chunks'
import { format } from 'date-fns'
import { filesize } from 'filesize'
import { cn } from '@/lib'
import { CONTENT_WIDTH_PREVIEW_OPEN, PREVIEW_WIDTH, TREE_HEIGHT } from './upload.constants'
import { searchAttachmentsByKey } from './upload.lib'

const UploadAdvancedContext = React.createContext<UploadAdvancedContextType<FileType | FolderType> | null>(null)

/**
 * Hook for accessing the context of the UploadAdvancedProvider.
 *
 * @returns {UploadAdvancedContextType<FileType | FolderType> | null} The context object.
 * @throws {Error} If the hook is used outside of an UploadAdvancedProvider.
 */
export const useUploadAdvancedContext = (): UploadAdvancedContextType<FileType | FolderType> => {
  const context = React.useContext(UploadAdvancedContext)
  if (!context) {
    throw new Error('useUploadContext must be used within an UploadProvider')
  }
  return context
}

/**
 * The UploadAdvancedProvider component provides the context for all the other components in the
 * UploadAdvanced layout. It wraps the components in a context provider and provides the state and
 * functions to manage the attachments and folders.
 *
 * @param {UploadAdvancedProviderProps} props - The props for the component.
 * @returns {JSX.Element} The rendered component.
 */
export const UploadAdvancedProvider = ({
  selectedFolder,
  attachments,
  className,
  currentBucket,
  children,
  ...props
}: UploadAdvancedProviderProps): JSX.Element => {
  const [_selectedFolder, setSelectedFolder] = React.useState<SelectedFolderType[]>(selectedFolder ?? [])
  const [_attachments, setAttachments] = React.useState<(FileType | FolderType)[]>(attachments ?? [])
  const [attachmentsState, setAttachmentsState] = React.useState<(FileType | FolderType)[]>([])
  const [previewFile, setPreviewFile] = React.useState<FileType | null>(null)
  const [uploadQuery, setUploadQuery] = React.useState<string>('')
  const [selectedAttachments, setSelectedAttachments] = React.useState<FileType[]>([])

  return (
    <UploadAdvancedContext.Provider
      value={{
        attachments: _attachments,
        setAttachments,
        attachmentsState,
        setAttachmentsState,
        selectedFolder: _selectedFolder,
        setSelectedFolder,
        previewFile,
        setPreviewFile,
        uploadQuery,
        setUploadQuery,
        selectedAttachments,
        setSelectedAttachments,
        currentBucket,
      }}
    >
      <div
        className={cn('flex flex-col w-full rounded-md bg-muted/10 border-border border overflow-hidden', className)}
        {...props}
      >
        {children}
      </div>
    </UploadAdvancedContext.Provider>
  )
}

/**
 * A component that renders the top bar of the advanced upload component.
 * Contains the upload actions and the multiple select layout.
 *
 * @returns A JSX.Element
 */
export const UploadAdvancedHeader = () => {
  return (
    <div className="w-full h-[45px] overflow-hidden relative">
      <UploadAdvancedActionsLayout />
      <UploadAdvancedMultiSelectLayout />
      <Separator />
    </div>
  )
}

/**
 * A component that renders the actions layout of the advanced upload component.
 * Contains the reload, view, upload file, add folder, and search buttons.
 *
 * @returns A JSX.Element
 */
export const UploadAdvancedActionsLayout = () => {
  const { selectedAttachments } = useUploadAdvancedContext()

  return (
    <>
      <div
        className={cn(
          'space-x-2 flex items-center place-content-end w-full m-0 p-2 transition-all duration-300 ease-in-out',
          selectedAttachments.length > 0 ? 'translate-y-[-42px]' : 'translate-y-0'
        )}
      >
        <UploadReloadButton />
        <UploadViewButton />
        <Separator
          orientation="vertical"
          className="h-6"
        />
        <UploadAdvancedButton />
        <UploadAddFolderButton />
        <Separator
          orientation="vertical"
          className="h-6"
        />
        <UploadSearchButton />
      </div>
    </>
  )
}

/**
 * A layout component for multi-select actions in the advanced upload interface.
 *
 * Displays a floating action bar when one or more attachments are selected,
 * providing options to download, move, or delete the selected attachments.
 *
 * The bar includes a close button to clear the selection and shows the count
 * of selected attachments. It appears with a smooth transition effect and hides
 * when no attachments are selected.
 */

export const UploadAdvancedMultiSelectLayout = () => {
  const { selectedAttachments, setSelectedAttachments } = useUploadAdvancedContext()

  return (
    <>
      <div
        className={cn(
          'absolute top-1/2 -translate-y-1/2 space-x-2 flex items-center w-full m-0 p-2 transition-all duration-300 ease-in-out bg-background pointer-events-all',
          selectedAttachments.length > 0 ? '' : 'opacity-0 t anslate-y-[-42px] pointer-events-none'
        )}
      >
        <Button
          size={'xs'}
          variant={'ghost'}
          className="p-1 h-auto"
          onClick={() => setSelectedAttachments([])}
          icon={{ children: X }}
        />
        <div className="flex items-center gap-3">
          <span className="text-xs text-muted-foreground">
            {selectedAttachments.length} Attachment
            {selectedAttachments.length === 1 ? ' is' : 's are'} selected
          </span>
          <Separator
            orientation="vertical"
            className="h-6"
          />
          <UploadDownloadAttachments itemsName={[...selectedAttachments.map(item => item.name)]} />
          <Separator
            orientation="vertical"
            className="h-6"
          />
          <UploadAlertMoveAction itemsName={[...selectedAttachments.map(item => item.name)]} />
          <UploadAlertDeleteAttachments
            itemsName={[...selectedAttachments.map(item => item.name)]}
            className={cn(
              buttonVariants({
                className: 'w-fit',
                size: 'xs',
                variant: 'destructive',
                border: 'destructive',
              })
            )}
            itemsToDelete={[...selectedAttachments.map(item => item.id)]}
          />
        </div>
      </div>
    </>
  )
}

/**
 * Component that displays the advanced content for file upload, including a preview of the uploaded file,
 * a scrollable area containing the file/folder tree, and tree extender elements for enhanced interaction.
 *
 * @returns {React.Element} The rendered component containing the file preview, tree, and scrollable area.
 */
export const UploadAdnvacedContent = React.memo(() => {
  const { previewFile } = useUploadAdvancedContext() ?? {}
  return (
    <div className="h-full relative">
      <UploadFilePreview />
      <ScrollArea className={cn(TREE_HEIGHT, previewFile && CONTENT_WIDTH_PREVIEW_OPEN)}>
        <div className="flex items-center h-full rounded-md relative overflow-hidden">
          <UploadAttachmentsTreeItem />
          <UploadTreeExtender />
        </div>
        <ScrollBar orientation="horizontal" />
      </ScrollArea>
    </div>
  )
})

/**
 * A component that extends the upload tree by rendering the contents of the selected folders.
 * It retrieves the folder content from the attachments and filters it based on the upload query.
 * Each folder's content is rendered as an UploadAttachmentsTreeItem component.
 *
 * @returns {JSX.Element | null} The rendered upload tree extender component, or null if no folders are selected.
 */

export const UploadTreeExtender = (): JSX.Element => {
  const { selectedFolder, attachments, uploadQuery } = useUploadAdvancedContext()

  return (
    <>
      {selectedFolder.length > 0 &&
        selectedFolder.map((folderContent, idx) => {
          const item = searchAttachmentsByKey(attachments, folder => folder.id === folderContent?.id, 'content')
          const filtered = !uploadQuery
            ? (item as FolderType)?.content
            : (item as FolderType)?.content.filter(item => item.name.toLowerCase().includes(uploadQuery.toLowerCase()))
          return (
            item && (
              <div
                key={item.id}
                className="flex items-center h-full rounded-md"
              >
                <UploadAttachmentsTreeItem
                  attachments={filtered}
                  key={item.id}
                />
                {idx !== selectedFolder.length - 1 && <Separator orientation="vertical" />}
              </div>
            )
          )
        })}
    </>
  )
}

/**
 * A component that renders a preview of the selected file.
 *
 * When a file is selected, the preview appears with a smooth transition effect and displays the file's name, type, size, created and updated at information.
 * The preview also includes a download button, a button to copy the file's URL to the clipboard, and a delete button.
 *
 * @returns {JSX.Element} The rendered file preview component.
 */
export const UploadFilePreview = (): JSX.Element => {
  const { previewFile, setPreviewFile } = useUploadAdvancedContext() ?? {}

  return (
    <>
      <div
        className={cn(
          PREVIEW_WIDTH,
          'absolute top-0 right-0 h-full duration-300 ease-in-out translate-x-[100%] z-10 dark:bg-[#121212] bg-card',
          previewFile && 'translate-x-0'
        )}
      >
        <ScrollArea className="h-full">
          <Button
            size={'xs'}
            variant={'nothing'}
            className="absolute top-2 right-4 p-0"
            icon={{ children: X }}
            onClick={() => setPreviewFile(null)}
          />
          <div className="border-l border-l-border bg-muted/10 w-full h-full px-4 py-8">
            <div className="border border-border w-full h-[180px] flex items-center justify-center rounded-md overflow-hidden">
              <img
                // src={URL.createObjectURL((previewFile?.file as Blob) ?? new Blob())}
                src={previewFile?.url}
                className="object-contain size-full"
                alt={previewFile?.name}
              />
            </div>
            <div className="my-4 flex flex-col gap-1">
              <h6 className="text-sm font-medium truncate max-w-[70%]">{previewFile?.name}</h6>
              <p className="text-accent-foreground/70 text-xs flex items-center gap-1 fno">
                <span>{previewFile?.type}</span>-
                <span>
                  {filesize(previewFile?.file ? +previewFile?.file.size : 0, {
                    round: 0,
                  })}
                </span>
              </p>
            </div>
            <div className="my-4 flex flex-col gap-1">
              <h6 className="text-xs font-medium text-accent-foreground/90">Created at</h6>
              <p className="text-accent-foreground/70 text-xs flex items-center gap-1 fno">
                {previewFile ? format(new Date(previewFile?.createdAt ?? Date.now()), 'dd/MM/yyyy hh:mm:ss a') : ''}
              </p>
            </div>
            <div className="my-4 flex flex-col gap-1">
              <h6 className="text-xs font-medium text-accent-foreground/90">Updated at</h6>
              <p className="text-accent-foreground/70 text-xs flex items-center gap-1 fno">
                {previewFile ? format(new Date(previewFile?.updatedAt ?? Date.now()), 'dd/MM/yyyy hh:mm:ss a') : ''}
              </p>
            </div>
            <div className="flex flex-row gap-2 [&_button]:px-3 mt-4 mb-2 ">
              <UploadDownloadAttachments itemsName={[previewFile?.name ?? '']} />
              <Button
                size={'xs'}
                // variant={'secondary'}
                icon={{ children: Clipboard }}
              >
                Get URL
              </Button>
            </div>
            <Separator />
            <div className="my-2 flex flex-row gap-2 [&_button]:px-3">
              <UploadAlertDeleteAttachments
                itemsName={[previewFile?.name ?? '']}
                className={cn(
                  buttonVariants({
                    className: 'w-fit',
                    size: 'xs',
                    variant: 'destructive',
                    border: 'destructive',
                  })
                )}
                itemsToDelete={[previewFile?.id ?? '']}
              />
            </div>
          </div>
        </ScrollArea>
      </div>
    </>
  )
}

'use client'

import * as React from 'react'
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from '@/registry/default/ui/dropdown-menu'
import { Input } from '@/registry/default/ui/input'
import { ScrollArea } from '@/registry/default/ui/scroll-area'
import { Button, buttonVariants } from '../button'
import {
  FileType,
  FolderType,
  SelectedFolderType,
  UploadAlertDeleteActionProps,
  UploadAlertMoveActionProps,
  UploadAttachmentsTreeItemProps,
  UploadDownloadAttachmentsProps,
  UploadRenameAttachmentButtonProps,
} from './upload.types'
import { CONTENT_POILERPLATE, FILE_TYPE_ICONS, TREE_HEIGHT, TREE_WIDTH } from './upload.constants'
import {
  addFolderToPath,
  advancedUploadAttachments,
  deleteAttachmentById,
  folderOpen,
  getFileType,
  moveAttachmentsToPath,
  renameAttachmentById,
  selectAttachmentFromFolderContent,
} from './upload.lib'
import {
  Alert,
  AlertDescription,
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
  AlertTitle,
  DialogClose,
  DialogDescription,
  DialogTitle,
} from '@/registry/default/ui'
import { Dialog, DialogContent, DialogFooter, DialogHeader, DialogTrigger } from '@/registry/default/ui'
import { DuckDropdownMenuItem } from '../dropdown-menu'
import { useUploadAdvancedContext } from './upload-advanced'
import { UploadOrDragSvg } from './upload.assets'
import { cn } from '@/lib/utils'
import {
  X,
  Download,
  Ellipsis,
  Folder,
  FolderOpen,
  Rows2,
  Search,
  FolderPlusIcon,
  AlertCircle,
  Trash,
  Move,
  RefreshCw,
  UploadIcon,
  Pencil,
} from 'lucide-react'
import { Checkbox, Separator } from '@/registry/default/ui'
import { debounceCallback } from '@/hooks'

/**
 * A button to reload the upload view.
 *
 * TODO: Implement reload functionality.
 * @returns {JSX.Element} The reload button.
 */
export const UploadReloadButton = (): JSX.Element => {
  // TODO: Implement reload functionality.
  return (
    <Button
      size={'xs'}
      icon={{ children: RefreshCw }}
      onClick={() => {}}
    >
      Reload
    </Button>
  )
}

/**
 * A dropdown menu button to view and sort the uploads.
 *
 * This component will render a dropdown menu with three sub-items:
 * - View: A radio button group to select the view type.
 * - Sort By: A radio button group to sort the uploads by a specific column.
 * - Sort Order: A radio button group to sort the uploads in ascending or descending order.
 *
 * @returns {JSX.Element} The dropdown menu button.
 */
export const UploadViewButton = (): JSX.Element => {
  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button
          size={'xs'}
          icon={{ children: Rows2 }}
        >
          View
        </Button>
      </DropdownMenuTrigger>

      <DropdownMenuContent>
        <DuckDropdownMenuItem
          title="View"
          content={CONTENT_POILERPLATE.view}
        />
        <DuckDropdownMenuItem
          title="Sort By"
          subgroup={true}
          content={CONTENT_POILERPLATE.sort}
        />
        <DuckDropdownMenuItem
          title="Sort Order"
          subgroup={true}
          content={CONTENT_POILERPLATE.order}
        />
      </DropdownMenuContent>
    </DropdownMenu>
  )
}

/**
 * A button component for advanced file uploads.
 *
 * Provides a file input for uploading multiple files with advanced handling.
 * Utilizes the `UploadManager.advancedUploadAttachments` function to manage
 * the upload process, including file validation and attachment state updates.
 *
 * @returns {JSX.Element} The upload button component.
 */

export const UploadAdvancedButton = (): JSX.Element => {
  const { setAttachments, selectedFolder, setSelectedFolder } = useUploadAdvancedContext() ?? {}

  const memoizedUploadFiles = React.useCallback(
    (e: React.ChangeEvent<HTMLInputElement>) => {
      advancedUploadAttachments({ e, selectedFolder, setSelectedFolder, setAttachments })
    },
    [selectedFolder, setSelectedFolder, setAttachments]
  )

  return (
    <Button
      className="relative"
      variant={'default'}
      size={'xs'}
      icon={{ children: UploadIcon }}
    >
      <Input
        placeholder="Filter files..."
        type="file"
        className="absolute w-full h-full opacity-0 cursor-pointer"
        multiple={true}
        onChange={e => memoizedUploadFiles(e)}
      />
      Upload file
    </Button>
  )
}

/**
 * A button component for creating a new folder.
 *
 * Provides a button and a dialog with an input field for creating a new folder.
 * Utilizes the `UploadManager.addFolderToPath` function to create the folder
 * and add it to the specified path.
 *
 * @returns {JSX.Element} The upload folder button component.
 */
export const UploadAddFolderButton = (): JSX.Element => {
  const { selectedFolder, setAttachments, setSelectedFolder } = useUploadAdvancedContext()
  const inputRef = React.useRef<HTMLInputElement | null>(null)

  return (
    <Dialog>
      <DialogTrigger asChild>
        <Button
          className="relative w-[1.625rem]"
          size={'xs'}
          icon={{ children: FolderPlusIcon }}
        >
          <span className="sr-only">Upload attachments</span>
        </Button>
      </DialogTrigger>
      <DialogContent>
        <div>
          <DialogHeader className="p-2">
            <DialogTitle className="text-lg font-medium pb-0">Create a new folder</DialogTitle>
            <DialogDescription className="text-sm text-muted-foreground">
              Enter the name of the folder you'd like to create.
            </DialogDescription>
          </DialogHeader>
          <Input
            placeholder="Enter folder name..."
            defaultValue={'New_folder' + Math.random().toString(36).slice(2)}
            className="h-[35px]"
            ref={inputRef}
          />
        </div>
        <DialogFooter>
          <DialogClose
            className={cn(buttonVariants({ className: 'px-8', size: 'sm', variant: 'outline' }))}
            onClick={() => inputRef.current && (inputRef.current.value = '')}
          >
            Cancel
          </DialogClose>
          <DialogClose
            className={cn(buttonVariants({ className: 'px-8', size: 'sm' }))}
            onClick={() =>
              addFolderToPath({
                selectedFolder,
                setAttachments,
                setSelectedFolder,
                folderName: inputRef.current?.value,
              })
            }
          >
            Submit
          </DialogClose>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  )
}

/**
 * @description A component to rename a single attachment.
 * @param {{ attachment: FileType | FolderType }} props
 * @returns {JSX.Element} The rename attachment button component.
 */
export const UploadRenameAttachments = ({ attachment }: UploadRenameAttachmentButtonProps): JSX.Element => {
  const { setAttachments } = useUploadAdvancedContext()
  const inputRef = React.useRef<HTMLInputElement | null>(null)

  return (
    <Dialog modal={true}>
      <DialogTrigger asChild>
        <Button
          className="relative w-[1.625rem]"
          size={'xs'}
          variant={'ghost'}
          icon={{ children: Pencil }}
        >
          <span className="">Rename </span>
        </Button>
      </DialogTrigger>
      <DialogContent>
        <div>
          <DialogHeader className="p-2">
            <DialogTitle className="text-lg font-medium pb-0">Rename the attachment</DialogTitle>
            <DialogDescription className="text-sm text-muted-foreground">
              Enter the name of the attachment you'd like to rename.
            </DialogDescription>
          </DialogHeader>
          <Input
            placeholder="Enter attachment name..."
            defaultValue={attachment.name}
            className="h-[35px]"
            ref={inputRef}
          />
        </div>
        <DialogFooter>
          <DialogClose
            className={cn(buttonVariants({ className: 'px-8', size: 'sm', variant: 'outline' }))}
            onClick={() => inputRef.current && (inputRef.current.value = '')}
          >
            Cancel
          </DialogClose>
          <DialogClose
            className={cn(buttonVariants({ className: 'px-8', size: 'sm' }))}
            onClick={() => renameAttachmentById(setAttachments, [attachment.id], inputRef.current?.value ?? '')}
          >
            Submit
          </DialogClose>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  )
}

/**
 * @description
 * A button that allows you to search for attachments in the current folder.
 * When clicked, it opens a text input to enter the search query.
 * The search query is debounced for 1000 milliseconds.
 * The search query is cleared when the button is clicked again.
 * @returns A JSX.Element
 */
export const UploadSearchButton = (): JSX.Element => {
  const [open, setOpen] = React.useState<boolean>(false)
  const { setUploadQuery } = useUploadAdvancedContext()
  const inputRef = React.useRef<HTMLInputElement | null>(null)

  React.useEffect(() => {
    inputRef.current?.focus()
  }, [open])

  const debounceSearch = debounceCallback(() => {
    inputRef.current?.value ? setUploadQuery(inputRef.current?.value) : setUploadQuery('')
  }, 1000)

  return (
    <div className="flex items-center">
      <Button
        size={'xs'}
        variant={'secondary'}
        border={'secondary'}
        icon={{ children: Search }}
        className={cn('relative w-[1.625rem] flex', open && 'hidden')}
        onClick={() => {
          setOpen(true)
        }}
      >
        <span className="sr-only">search</span>
      </Button>
      <div
        className={cn(
          buttonVariants({
            variant: 'secondary',
            border: 'secondary',
            size: 'xs',
            className: 'relative h-[1.625rem] overflow-hidden w-[200px] hidden [&_svg]:pointer-events-auto',
          }),
          open && 'flex'
        )}
      >
        <Search className="absolute top-1/2 -translate-y-1/2 left-2 size-[0.875rem] z-10" />
        <Input
          autoFocus={true}
          onChange={_ => debounceSearch()}
          className="pl-6 w-[200px] h-[1.625rem] text-xs text-accent-foreground/50 bg-transparent placeholder:text-xs placeholder:text-accent-foreground/50"
          placeholder="Search file or folder..."
          ref={inputRef}
        />
        <X
          className="absolute top-1/2 -translate-y-1/2 right-2 size-[0.875rem] stroke-[2px] cursor-pointer z-10 pointer-events-auto"
          onClick={() => {
            setUploadQuery('')
            inputRef.current && (inputRef.current.value = '')
            setOpen(false)
          }}
        />
      </div>
    </div>
  )
}

export const UploadDownloadAttachments = React.memo(
  ({ itemsName, size, withinDropdown = false, ...props }: UploadDownloadAttachmentsProps): JSX.Element => {
    const { currentBucket } = useUploadAdvancedContext()

    const ButtonSubmit = (
      <AlertDialogAction
        className={cn(buttonVariants({ className: 'px-8', size: 'sm' }))}
        onClick={() => {}}
      >
        Download
      </AlertDialogAction>
    )

    return (
      <AlertDialog>
        <AlertDialogTrigger asChild>
          <Button
            size={size ?? 'xs'}
            icon={{ children: Download }}
            onClick={() => {}}
            {...props}
          >
            Download
          </Button>
        </AlertDialogTrigger>
        <AlertDialogContent className="p-2">
          <AlertDialogHeader>
            <h5 className="text-lg font-medium p-4 pb-0">
              Download
              <Button
                variant={'nothing'}
                className="py-0 px-2 text-lg"
                label={{
                  children: (
                    <div className="flex flex-col sapce-y-2 p-1">
                      {itemsName.map((item, index) => (
                        <span key={index}>{item}</span>
                      ))}
                    </div>
                  ),
                  side: 'top',
                  showLabel: true,
                }}
              >
                <span className="font-mono italic underline underline-offset-4">
                  {itemsName.length > 1
                    ? `${itemsName.length} file${itemsName[0].length > 1 ? 's' : ''}`
                    : `${itemsName[0]?.slice(0, 15)}${itemsName[0]?.length > 15 ? '... ' : ''}`}
                </span>
              </Button>
              from
              <Button
                variant={'nothing'}
                className="py-0 px-2 text-lg"
                label={{
                  children: currentBucket ? currentBucket : 'the bucket',
                  showLabel: currentBucket.length > 5 ? true : false,
                  side: 'top',
                }}
              >
                <span className="font-mono italic underline underline-offset-4">
                  {currentBucket
                    ? `${currentBucket.length > 5 ? `${currentBucket.slice(0, 5)}...` : currentBucket}`
                    : 'the bucket'}
                </span>
              </Button>
            </h5>
            <p className="text-sm text-muted-foreground px-4 !mt-0">Select the items you'd like to download.</p>

            <Separator />
          </AlertDialogHeader>

          <AlertDialogFooter>
            <AlertDialogCancel className={cn(buttonVariants({ variant: 'outline', className: 'px-8', size: 'sm' }))}>
              Cancel
            </AlertDialogCancel>
            {withinDropdown ? <DropdownMenuItem className="p-0">{ButtonSubmit}</DropdownMenuItem> : ButtonSubmit}
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    )
  }
)

export const UploadAlertMoveAction = React.memo(
  ({ itemsName: itemName, ...props }: UploadAlertMoveActionProps): JSX.Element => {
    const { currentBucket, selectedAttachments, setSelectedAttachments, setAttachments } = useUploadAdvancedContext()
