'use client'

import React from 'react'
import { ScrollBar, Separator } from '@/registry/default/ui'
import { Input } from '@/registry/default/ui'
import { ScrollArea } from '@/registry/default/ui'
import { Button, buttonVariants } from '../button'
import { Download, Trash, Upload as UploadIcon, Clipboard, X, RefreshCw } from 'lucide-react'
import { AttachmentType, FolderType, SelectedFolderType, UploadAdvancedContextType } from './upload.types'
import { deleteFromFolderContent, mergeAttachmentPath, searchNestedArrayByKey, uploadFiles } from './upload.lib'
import {
  FolderButton,
  UploadAlertDeleteAction,
  UploadAlertMoveAction,
  UploadAttachmentsTreeItem,
  UploadSearch,
  UploadViewButton,
} from './upload-chunks'
import { format } from 'date-fns'
import { filesize } from 'filesize'
import { cn } from '@/lib'

const UploadAdvancedContext = React.createContext<UploadAdvancedContextType<AttachmentType | FolderType> | null>(null)

export const useUploadAdvancedContext = () => {
  const context = React.useContext(UploadAdvancedContext)
  if (!context) {
    throw new Error('useUploadContext must be used within an UploadProvider')
  }
  return context
}

export interface UploadAdvancedProviderProps extends React.HTMLProps<HTMLDivElement> {
  selectedFolder?: SelectedFolderType[]
  attachments?: (AttachmentType | FolderType)[]
}

export const UploadAdvancedProvider = ({
  selectedFolder,
  attachments,
  className,
  children,
  ...props
}: UploadAdvancedProviderProps) => {
  const [_selectedFolder, setSelectedFolder] = React.useState<SelectedFolderType[]>(selectedFolder ?? [])
  const [_attachments, setAttachments] = React.useState<(AttachmentType | FolderType)[]>(attachments ?? [])
  const [attachmentsState, setAttachmentsState] = React.useState<(AttachmentType | FolderType)[]>([])
  const [previewFile, setPreviewFile] = React.useState<AttachmentType | null>(null)
  const [uploadQuery, setUploadQuery] = React.useState<string>('')
  const [selecttedAttachment, setSelectedAttachment] = React.useState<AttachmentType[]>([])

  return (
    <UploadAdvancedContext.Provider
      value={{
        attachments: _attachments,
        setAttachments,
        attachmentsState,
        setAttachmentsState,
        selectedFolder: _selectedFolder,
        setSelectedFolder,
        previewFile,
        setPreviewFile,
        uploadQuery,
        setUploadQuery,
        selecttedAttachment,
        setSelectedAttachment,
      }}
    >
      <div
        className={cn(
          'flex flex-col w-full rounded-md bg-muted/10 border-border border h-[600px] overflow-hidden',
          className
        )}
        {...props}
      >
        {children}
      </div>
    </UploadAdvancedContext.Provider>
  )
}

export const UploadAdvancedHeader = React.memo(() => {
  const { selecttedAttachment, setSelectedAttachment, setAttachments, setSelectedFolder } = useUploadAdvancedContext()

  return (
    <div className="w-full h-[45px] overflow-hidden relative">
      <div
        className={cn(
          'space-x-2 flex items-center place-content-end w-full m-0 p-2 transition-all duration-300 ease-in-out',
          selecttedAttachment.length > 0 ? 'translate-y-[-42px]' : 'translate-y-0'
        )}
      >
        <Button
          size={'xs'}
          icon={{ children: RefreshCw }}
        >
          Reload
        </Button>
        <UploadViewButton />
        <Separator
          orientation="vertical"
          className="h-6"
        />
        <UploadAdvancedButton />
        <FolderButton />
        <Separator
          orientation="vertical"
          className="h-6"
        />
        <UploadSearch />
      </div>
      <div
        className={cn(
          'absolute top-1/2 -translate-y-1/2 space-x-2 flex items-center w-full m-0 p-2 transition-all duration-300 ease-in-out bg-background pointer-events-all',
          selecttedAttachment.length > 0 ? '' : 'opacity-0 t anslate-y-[-42px] pointer-events-none'
        )}
      >
        <Button
          size={'xs'}
          variant={'ghost'}
          className="p-1 h-auto"
          onClick={() => setSelectedAttachment([])}
          icon={{ children: X }}
        />
        <div className="flex items-center gap-3">
          <span className="text-xs text-muted-foreground">
            {selecttedAttachment.length} Attachment
            {selecttedAttachment.length === 1 ? ' is' : 's are'} selected
          </span>
          <Separator
            orientation="vertical"
            className="h-6"
          />
          <Button
            size={'xs'}
            icon={{ children: Download }}
          >
            Download
          </Button>
          <Separator
            orientation="vertical"
            className="h-6"
          />
          <UploadAlertMoveAction
            itemName="duck-ui-bucket"
            onContinue={(_, path) =>
              mergeAttachmentPath(setAttachments, setSelectedAttachment, selecttedAttachment, path)
            }
          />
          <UploadAlertDeleteAction
            itemName={`${selecttedAttachment.length} item${
              selecttedAttachment.length === 1 ? '' : 's'
            } out of wildduck_bucket`}
            className={cn(
              buttonVariants({
                className: 'w-fit',
                size: 'xs',
                variant: 'destructive',
                border: 'destructive',
              })
            )}
            onCancel={() => {}}
            onContinue={() => {
              setAttachments(old => {
                return deleteFromFolderContent(old, [...selecttedAttachment.map(item => item.id)])
              })
              setSelectedAttachment([])
            }}
          />
        </div>
      </div>
    </div>
  )
})

export const UploadAdvancedButton = () => {
  const { setAttachments, selectedFolder, setSelectedFolder } = useUploadAdvancedContext() ?? {}

  const memoizedUploadFiles = React.useCallback(
    (e: React.ChangeEvent<HTMLInputElement>) => {
      uploadFiles({ e, selectedFolder, setSelectedFolder, setAttachments })
    },
    [selectedFolder, setSelectedFolder, setAttachments]
  )
  return (
    <>
      <Button
        className="relative "
        variant={'default'}
        size={'xs'}
        icon={{ children: UploadIcon }}
      >
        <Input
          placeholder="Filter files..."
          type="file"
          className="absolute w-full h-full opacity-0 cursor-pointer"
          multiple={true}
          onChange={e => memoizedUploadFiles(e)}
        />
        Upload file
      </Button>
    </>
  )
}

export const UploadAdnvacedContent = React.memo(() => {
  return (
    <div className="h-full relative">
      <UploadFilePreview />
      <ScrollArea className="h-full">
        <div className="flex items-center h-full rounded-md relative overflow-hidden">
          <UploadAttachmentsTreeItem />
          <UploadTreeExtender />
        </div>
        <ScrollBar orientation="horizontal" />
      </ScrollArea>
    </div>
  )
})

export const UploadTreeExtender = () => {
  const { selectedFolder, attachments, uploadQuery } = useUploadAdvancedContext() ?? {}

  return (
    selectedFolder.length > 0 &&
    selectedFolder.map((folderContent, idx) => {
      const item = searchNestedArrayByKey(attachments, folder => folder.id === folderContent?.id, 'content')
      const filtered = !uploadQuery
        ? (item as FolderType)?.content
        : (item as FolderType)?.content.filter(item => item.name.toLowerCase().includes(uploadQuery.toLowerCase()))
      return (
        item && (
          <div
            key={item.id}
            className="flex items-center h-full rounded-md"
          >
            <UploadAttachmentsTreeItem
              attachments={filtered}
              key={item.id}
            />
            {idx !== selectedFolder.length - 1 && <Separator orientation="vertical" />}
          </div>
        )
      )
    })
  )
}

export const UploadFilePreview = React.memo(() => {
  const { previewFile, setPreviewFile } = useUploadAdvancedContext() ?? {}
  return (
    <>
      <div
        className={cn(
          'absolute top-0 right-0 h-full w-[400px] duration-300 ease-in-out translate-x-[100%] z-10 dark:bg-[#121212] bg-card',
          previewFile && 'translate-x-0'
        )}
      >
        <ScrollArea className="h-full">
          <Button
            size={'xs'}
            variant={'nothing'}
            className="absolute top-2 right-4 p-0"
            icon={{ children: X }}
            onClick={() => setPreviewFile(null)}
          />
          <div className="border-l border-l-border bg-muted/10 w-full h-full px-4 py-8">
            <div className="border border-border w-full h-[180px] flex items-center justify-center rounded-md overflow-hidden">
              <img
                src={URL.createObjectURL((previewFile?.file as Blob) ?? new Blob())}
                className="object-contain size-full"
                alt={previewFile?.name}
              />
            </div>
            <div className="my-4 flex flex-col gap-1">
              <h6 className="text-sm font-medium truncate max-w-[70%]">{previewFile?.name}</h6>
              <p className="text-accent-foreground/70 text-xs flex items-center gap-1 fno">
                <span>{previewFile?.type}</span>-
                <span>
                  {filesize(previewFile?.file ? +previewFile?.file.size : 0, {
                    round: 0,
                  })}
                </span>
              </p>
            </div>
            <div className="my-4 flex flex-col gap-1">
              <h6 className="text-xs font-medium text-accent-foreground/90">Created at</h6>
              <p className="text-accent-foreground/70 text-xs flex items-center gap-1 fno">
                {previewFile ? format(new Date(previewFile?.createdAt ?? Date.now()), 'dd/MM/yyyy hh:mm:ss a') : ''}
              </p>
            </div>
            <div className="my-4 flex flex-col gap-1">
              <h6 className="text-xs font-medium text-accent-foreground/90">Updated at</h6>
              <p className="text-accent-foreground/70 text-xs flex items-center gap-1 fno">
                {previewFile ? format(new Date(previewFile?.updatedAt ?? Date.now()), 'dd/MM/yyyy hh:mm:ss a') : ''}
              </p>
            </div>
            <div className="my-4 flex flex-row gap-2 [&_button]:px-3 mt-4">
              <Button
                size={'xs'}
                variant={'secondary'}
                // border={'muted'}
                icon={{ children: Download }}
              >
                Download
              </Button>
              <Button
                size={'xs'}
                variant={'secondary'}
                // border={'primary'}
                icon={{ children: Clipboard }}
              >
                Get URL
              </Button>
            </div>
            <Separator />
            <div className="my-4 flex flex-row gap-2 [&_button]:px-3">
              <Button
                size={'xs'}
                variant={'destructive'}
                border={'destructive'}
                icon={{ children: Trash }}
              >
                Delete
              </Button>
            </div>
          </div>
        </ScrollArea>
      </div>

      <Separator />
    </>
  )
})

'use client'

import * as React from 'react'

import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from '@/registry/default/ui/dropdown-menu'
import { Input } from '@/registry/default/ui/input'
import { ScrollArea } from '@/registry/default/ui/scroll-area'
import { Button, buttonVariants, CommandType } from '../button'

import { AttachmentType, FolderType, SelectedFolderType, UploadAttachmentsTreeItemProps } from './upload.types'
import { CONTENT_POILERPLATE, FILE_TYPE_ICONS } from './upload.constants'
import { addFolderToPath, deleteFromFolderContent, folderOpen, getFileType, renameInFolderContent } from './upload.lib'
import {
  Alert,
  AlertDescription,
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
  AlertTitle,
  DialogClose,
  DialogDescription,
  DialogTitle,
} from '@/registry/default/ui'
import { Dialog, DialogContent, DialogFooter, DialogHeader, DialogTrigger } from '@/registry/default/ui'
import { DuckDropdownMenuItem } from '../dropdown-menu'
import { useUploadAdvancedContext } from './upload-advanced'
import { UploadOrDragSvg } from './upload.assets'

import { cn } from '@/lib/utils'

import {
  X,
  Download,
  Ellipsis,
  Folder,
  FolderOpen,
  Rows2,
  Search,
  FolderPlusIcon,
  Pencil,
  AlertCircle,
  Trash,
  Move,
  LucideMinusSquare,
} from 'lucide-react'
import { Checkbox, Separator } from '@/registry/default/ui'
import { debounceCallback } from '@/hooks'

export const UploadSearch = () => {
  const [open, setOpen] = React.useState<boolean>(false)
  const { setUploadQuery } = useUploadAdvancedContext()
  const inputRef = React.useRef<HTMLInputElement | null>(null)

  React.useEffect(() => {
    inputRef.current?.focus()
  }, [open])

  const debounceSearch = debounceCallback(() => {
    inputRef.current?.value ? setUploadQuery(inputRef.current?.value) : setUploadQuery('')
  }, 1000)

  return (
    <div className="flex items-center">
      <Button
        size={'xs'}
        variant={'muted'}
        border={'muted'}
        icon={{ children: Search }}
        className={cn('relative w-[1.625rem] flex', open && 'hidden')}
        onClick={() => {
          setOpen(true)
        }}
      >
        <span className="sr-only">search</span>
      </Button>
      <div
        className={cn(
          buttonVariants({
            variant: 'muted',
            border: 'muted',
            size: 'xs',
            className: 'relative h-[1.625rem] overflow-hidden w-[200px] hidden [&_svg]:pointer-events-auto',
          }),
          open && ' flex'
        )}
      >
        <Search className="absolute top-1/2 -translate-y-1/2 left-2 size-[0.875rem] z-10" />
        <Input
          autoFocus={true}
          onChange={_ => debounceSearch()}
          className="pl-6 w-[200px] h-[1.625rem] text-xs text-accent-foreground/50 bg-transparent placeholder:text-xs placeholder:text-accent-foreground/50"
          placeholder="Search file or folder..."
          ref={inputRef}
        />
        <X
          className="absolute top-1/2 -translate-y-1/2 right-2 size-[0.875rem] stroke-[2px] cursor-pointer z-10 pointer-events-auto"
          onClick={() => {
            setUploadQuery('')
            inputRef.current && (inputRef.current.value = '')
            setOpen(false)
          }}
        />
      </div>
    </div>
  )
}

export const FolderButton = () => {
  const { selectedFolder, setAttachments, setSelectedFolder } = useUploadAdvancedContext()
  const inputRef = React.useRef<HTMLInputElement | null>(null)
  return (
    <Dialog>
      <DialogTrigger asChild>
        <Button
          className="relative w-[1.625rem]"
          size={'xs'}
          icon={{
            children: FolderPlusIcon,
          }}
        />
      </DialogTrigger>
      <DialogContent>
        <div>
          <DialogHeader className="p-2">
            <DialogTitle className="text-lg font-medium pb-0">Create a new folder</DialogTitle>
            <DialogDescription className="text-sm text-muted-foreground">
              Enter the name of the folder you'd like to create.
            </DialogDescription>
          </DialogHeader>
          <Input
            placeholder="Enter folder name..."
            defaultValue={'New_folder' + Math.random().toString(36).slice(2)}
            className="h-[30x]"
            ref={inputRef}
          />
        </div>
        <DialogFooter>
          <DialogClose
            className={cn(buttonVariants({ className: 'px-8', size: 'sm', variant: 'outline' }))}
            onClick={() => inputRef.current && (inputRef.current.value = '')}
          >
            Cancel
          </DialogClose>
          <DialogClose
            className={cn(buttonVariants({ className: 'px-8', size: 'sm' }))}
            onClick={() =>
              addFolderToPath({
                selectedFolder,
                setAttachments,
                setSelectedFolder,
                folderName: inputRef.current?.value ?? 'New_Folder' + Math.random().toString(36).slice(2),
              })
            }
          >
            Submit
          </DialogClose>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  )
}

export const UploadAttachmentsTreeItem = React.memo(({ attachments }: UploadAttachmentsTreeItemProps) => {
  const { selectedFolder, setSelectedFolder, attachments: _attachments, uploadQuery } = useUploadAdvancedContext()

  const filtered = (
    uploadQuery
      ? (attachments ?? _attachments)?.filter(item => item.name.toLowerCase().includes(uploadQuery.toLowerCase()))
      : (attachments ?? _attachments)
  ) as (AttachmentType | FolderType)[]

  return (filtered ?? _attachments)?.length > 0 ? (
    <div className="flex items-start h-full rounded-md">
      <div className="flex flex-col h-full rounded-md">
        <UploadSelectAll attachments={attachments ?? _attachments} />
        <ScrollArea className="h-full rounded-md w-[250px] p-2 bg-muted/10">
          <div className="flex flex-col gap-1 h-full">
            {filtered.map(attachment => {
              if ((attachment as AttachmentType).file) {
                return (
                  <UploadAttachmentFile
                    attachmentFile={attachment as AttachmentType}
                    key={attachment.id}
                  />
                )
              }
              return (
                <UploadAttachmentFolder
                  key={attachment.id}
                  attachmentFolder={attachment as FolderType}
                  selected={selectedFolder}
                  setSelected={setSelectedFolder}
                />
              )
            })}
          </div>
        </ScrollArea>
      </div>
      <Separator orientation="vertical" />
    </div>
  ) : (
    <EmptyFolder />
  )
})

export const UploadSelectAll = React.memo((props: { attachments: (AttachmentType | FolderType)[] }) => {
  const { attachments } = props
  const { setSelectedAttachment, selecttedAttachment } = useUploadAdvancedContext()
  const currentTreeLevel = attachments?.[0]?.treeLevel

  // Get all selected folders in the current tree
  const selectedInCurrentTree = selecttedAttachment.filter(attachment => attachment.treeLevel === currentTreeLevel)
  const filesInCurrentTree = attachments.filter(item => !(item as FolderType)?.content)

  // Determine the `isChecked` state
  const isChecked =
    selectedInCurrentTree.length === filesInCurrentTree?.length
      ? true // All selected
      : selectedInCurrentTree.length > 0
        ? 'indeterminate' // Some selected
        : false // None selected

  return (
    <div
      className={cn(
        'flex items-center gap-2 p-2 bg-muted rounded-md mx-2 mt-2 -mb-1 transition-all duration-300 ease-in-out',
        (selecttedAttachment.length === 0 || filesInCurrentTree?.length === 0) && '-mt-10 mb-2'
      )}
    >
      <Checkbox
        className="w-[15px] h-[15px] border-muted-foreground/80"
        onCheckedChange={_ => {
          setSelectedAttachment(prevSelected => {
            const allFilesSelected = filesInCurrentTree.every(file =>
              prevSelected.some(attachment => attachment.id === file.id)
            )

            // If all files are already selected, remove them
            if (allFilesSelected)
              return prevSelected.filter(attachment => !filesInCurrentTree.some(file => file.id === attachment.id))
            // Otherwise, add the files that are not already selected
            const newFiles = filesInCurrentTree.filter(
              file => !prevSelected.some(attachment => attachment.id === file.id)
            )
            return [...prevSelected, ...newFiles]
          })
        }}
        checked={isChecked}
      />
      <span className="text-xs font-medium text-muted-foreground/80">
        {isChecked === true
          ? `All ${filesInCurrentTree?.length} file${selectedInCurrentTree.length === 1 ? ' is' : 's are'} selected`
          : isChecked === 'indeterminate'
            ? `${selectedInCurrentTree.length} file${selectedInCurrentTree.length === 1 ? ' is' : 's are'} selected`
            : `Select all ${filesInCurrentTree?.length} file${filesInCurrentTree.length === 1 ? '' : 's'}`}
      </span>
    </div>
  )
})

/*
 * Empty Folder
 */
export const EmptyFolder = () => {
  return (
    <div className="border-r border-r-border bg-muted/10 w-[250px] h-full p-4 flex items-center flex-col space-y-2 justify-center">
      <UploadOrDragSvg className="size-[100px]" />
      <p className="text-center w-full text-sm font-medium">Drop your files here</p>
      <p className="text-accent-foreground/70 text-center w-full text-xs max-w-[150px]">
        Or upload them via the "Upload file" button above
      </p>
    </div>
  )
}

export const UploadAttachmentFolder = React.memo(
  (props: {
    attachmentFolder: FolderType
    selected: SelectedFolderType[]
    setSelected: React.Dispatch<React.SetStateAction<SelectedFolderType[]>>
  }) => {
    const { attachmentFolder, selected, setSelected } = props
    const { setAttachments } = useUploadAdvancedContext()
    const exist_in_tree = selected?.some(item => item.id === attachmentFolder.id)

    return (
      <div className="relative">
        <div
          className={cn(
            'relative bg-card-foreground/5 rounded-md overflow-hidden w-full flex items-center justify-start gap-2 p-2 hover:bg-card-foreground/15 transition-all cursor-pointer [&_*]:select-none',
            exist_in_tree && 'bg-card-foreground/15'
          )}
          onClick={() => folderOpen({ attachmentFolder, setSelected, exist_in_tree })}
        >
          <div className="relative [&_svg]:size-4">
            {exist_in_tree ? <FolderOpen /> : <Folder className={cn(attachmentFolder.files > 0 && 'fill-white')} />}
          </div>
          <h6 className="text-xs font-medium truncate max-w-[70%]">{attachmentFolder.name} </h6>
        </div>

        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <Button
              size={'xs'}
              variant={'ghost'}
              className="h-4 w-6 absolute top-1/2 right-2 -translate-y-1/2"
              icon={{ children: Ellipsis }}
            />
          </DropdownMenuTrigger>

          <DropdownMenuContent className="">
            <div className="flex flex-col items-start justify-start [&_button]:justify-between [&_button]:w-full [&_button]:rounded-sm [&>div]:p-0 [&>div]:justify-between [&>div]:flex [&>div]:items-center [&>div]:w-full">
              <div>
                <UploadAlertDeleteAction
                  itemName={attachmentFolder.name + ' folder'}
                  command={{
                    label: 'Alt+D',
                    key: 'Alt+d',
                    variant: 'nothing',
                    className: 'text-accent-foreground/40 w-full ml-6',
                  }}
                  onCancel={() => {}}
                  onContinue={() => setAttachments(old => deleteFromFolderContent(old, [attachmentFolder.id]))}
                />
              </div>
            </div>
          </DropdownMenuContent>
        </DropdownMenu>
      </div>
    )
  }
)

export const UploadAttachmentFile = React.memo(({ attachmentFile }: { attachmentFile: AttachmentType }) => {
  const fileType = getFileType(attachmentFile.file)
  const { setAttachments, setPreviewFile, selecttedAttachment, setSelectedAttachment } = useUploadAdvancedContext()
  const exist_in_selected = selecttedAttachment.some(attachment => attachment.id === attachmentFile.id)

  return (
    <div className="relative group/file">
      <div
        className={cn(
          'relative bg-card-foreground/5 rounded-md overflow-hidden w-full flex items-center justify-start gap-2 p-2 hover:bg-card-foreground/15 transition-all cursor-pointer'
        )}
        onClick={() => setPreviewFile(attachmentFile)}
      >
        <div
          className={cn(
            'relative [&_svg]:size-4 group-hover/file:opacity-0 opacity-100',
            exist_in_selected && 'opacity-0'
          )}
        >
          {FILE_TYPE_ICONS[fileType]}
        </div>
        <h6 className="text-xs font-medium truncate max-w-[70%]">{attachmentFile.name}</h6>
      </div>

      <Checkbox
        className={cn(
          'absolute top-1/2 left-2 -translate-y-1/2 group-hover/file:opacity-100 opacity-0 w-[15px] h-[15px]',
          exist_in_selected && '!opacity-100'
        )}
        checked={exist_in_selected}
        onCheckedChange={e => {
          if (e) return setSelectedAttachment(prev => [...prev, attachmentFile])
          setSelectedAttachment(prev => prev.filter(attachment => attachment.id !== attachmentFile.id))
        }}
      />
      <DropdownMenu modal={true}>
        <DropdownMenuTrigger asChild>
          <Button
            size={'xs'}
            variant={'ghost'}
            className="h-4 w-6 absolute top-1/2 right-2 -translate-y-1/2"
            icon={{ children: Ellipsis }}
          />
        </DropdownMenuTrigger>

        <DropdownMenuContent className="">
          <div className="flex flex-col items-start justify-start [&_button]:justify-between [&_button]:w-full [&_button]:rounded-sm [&>div]:p-0 [&>div]:justify-between [&>div]:flex [&>div]:items-center [&>div]:w-full">
            <DropdownMenuItem>
              <Button
                size={'xs'}
                variant={'ghost'}
                onClick={() => setAttachments(old => renameInFolderContent(old, [attachmentFile.id], 'New Name'))}
                icon={{ children: Pencil, className: 'h-4 w-4' }}
              >
                Rename
              </Button>
            </DropdownMenuItem>
            <DropdownMenuItem>
              <Button
                size={'xs'}
                variant={'ghost'}
                onClick={() => {}}
                icon={{ children: Download, className: 'h-4 w-4' }}
              >
                Download
              </Button>
            </DropdownMenuItem>
            <div>
              <UploadAlertDeleteAction
                itemName={attachmentFile.name}
                command={{
                  label: 'Alt+D',
                  key: 'Alt+d',
                  variant: 'nothing',
                  className: 'text-accent-foreground/40 w-full ml-6',
                }}
                onCancel={() => {}}
                onContinue={() => setAttachments(old => deleteFromFolderContent(old, [attachmentFile.id]))}
              />
