  return (
    <Button
      className="relative"
      variant={'outline'}
      size={'sm'}
      icon={{ children: UploadIcon }}
    >
      <Input
        placeholder="Filter files..."
        type="file"
        className="absolute w-full h-full opacity-0 cursor-pointer"
        multiple={true}
        onChange={e => handleAttachment({ e, setAttachmentsState: setAttachments })}
      />
