import { Progress } from '@/registry/default/ui'
import { Check, CheckCircle, CheckCircleIcon, CircleCheck, Loader, Loader2 } from 'lucide-react'
import { toast } from 'sonner'
import { Button } from '../button'

export default function SonnerUpload() {
  const uploadPromise = (files: number, toastId: number) => {
    toast.loading(
      <UploadContent
        progress={0}
        files={files}
      />,
      {
        duration: 400000,
        id: toastId,
      }
    )

    return new Promise(resolve => {
      let currentProgress = 0

      toast.loading(
        <UploadContent
          progress={currentProgress}
          files={files}
        />,
        {
          duration: 400000,
          id: toastId,
        }
      )

      const intervalId = setInterval(() => {
        currentProgress += Math.floor(Math.random() * 10) + 1 // Increment progress by a random value
        if (currentProgress > 100) currentProgress = 100 // Ensure progress does not exceed 100%

        if (currentProgress >= 100) {
          clearInterval(intervalId) // Clear the interval once upload is complete
          resolve({ progress: currentProgress, message: 'Upload complete', files: 3, toastId }) // Resolve the promise when progress reaches 100
        }

        toast.loading(
          <UploadContent
            progress={currentProgress}
            files={files}
          />,
          {
            id: toastId,
          }
        )
      }, 20) // Adjust the interval time as needed
    })
  }

  const UploadContent = ({ progress, files }: { progress: number; files: number }) => (
    <div className="flex gap-3 w-full">
      {progress >= 100 ? (
        <CircleCheck
          className="fill-foreground [&_path]:fill-red-500"
          size={16}
        />
      ) : (
        <Loader
          className="animate-spin text-foreground-muted mt-0.5 "
          size={16}
        />
      )}
      <div className="flex flex-col gap-2 w-full">
        <div className="flex w-full justify-between">
          <p className="text-foreground text-sm">
            {progress >= 100 ? `Upload complete` : files ? `Uploading ${files} files...` : `Uploading...`}
          </p>
          <p className="text-foreground-light text-sm font-mono">{`${progress}%`}</p>
        </div>
        <Progress
          value={progress}
          className="w-full h-1"
        />
        <small className="text-foreground-muted text-xs">Please do not close the browser until completed</small>
      </div>
    </div>
  )

  return (
    <div className="flex flex-col gap-3">
      <Button
        variant="default"
        onClick={async () => {
          // random id
          const toastId = Math.random()
          const max = 20 // specify the maximum value
          const files = Math.floor(Math.random() * max)
          try {
            const promise = await uploadPromise(files, toastId)
            toast.success(
              <UploadContent
                progress={promise.progress}
                files={promise.files}
              />,
              {
                duration: 2000,
                id: toastId,
              }
            )
          } catch (error) {}
        }}
      >
        Start upload
      </Button>
    </div>
  )
}

import { toast } from 'sonner'
import { FileType, MAX_SIZE } from './upload.constants'
import {
  AttachmentType,
  FolderType,
  HandleAttachmentProps,
  UploadFilesArgs,
  UploadPromiseArgs,
  UploadPromiseReturn,
} from './upload.types'
import { uuidv7 } from 'uuidv7'
import { UploadSonnerContent } from './upload'

export const uploadPromise = ({ files, toastId }: UploadPromiseArgs): Promise<UploadPromiseReturn> => {
  return new Promise(resolve => {
    let currentProgress = 0
    const remainingTime = getRemainingTime(currentProgress, 100)

    // Show initial toast loading message
    toast.loading(
      <UploadSonnerContent
        progress={currentProgress}
        remainingTime={remainingTime}
        files={files}
      />,
      { id: toastId }
    )

    // Simulate progress update with interval
    const intervalId = setInterval(() => {
      currentProgress += Math.floor(Math.random() * 10) + 1
      if (currentProgress > 100) currentProgress = 100

      // Update toast content with the new progress and remaining time
      toast.loading(
        <UploadSonnerContent
          progress={currentProgress}
          remainingTime={remainingTime}
          files={files}
        />,
        { id: toastId }
      )

      // Resolve the promise once progress reaches 100%
      if (currentProgress >= 100) {
        clearInterval(intervalId)
        resolve({ progress: currentProgress, files, remainingTime })
      }
    }, 200)
  })
}

// Helper function to calculate remaining time
export function getRemainingTime(currentProgress: number, maxProgress: number) {
  const progressPercentage = (currentProgress / maxProgress) * 100
  const calculatedRemainingTime = 200 - progressPercentage * 2
  return calculatedRemainingTime > 0 ? calculatedRemainingTime : 0
}

export async function uploadFiles(props: UploadFilesArgs) {
  const { e, selectedFolder, setSelectedFolder, setAttachments } = props

  try {
    const files = e.currentTarget.files

    if (!files) return toast.error('Please select a file')

    const newAttachments: AttachmentType[] = []

    for (let i = 0; i < files.length; i++) {
      const file = files[i]

      if (file.size > MAX_SIZE) {
        toast.error(`File has exceeded the max size: ${file.name.slice(0, 15)}...`)
        continue // Skip this file and continue with the next
      }

      const attachment: AttachmentType = {
        id: uuidv7(),
        file: file,
        name: file.name,
        url: null,
        type: file.type,
        size: file.size.toString(),
      }

      newAttachments.push(attachment)
    }

    // random id
    const toastId = Math.random()

    // Upload promise
    const promise = await uploadPromise({ files: files.length, toastId })

    // Show upload progress toast
    promise &&
      toast.success(`Successfully Uploaded ${files.length} file${files.length > 1 ? 's' : ''}`, {
        duration: 2000,
        id: toastId,
      })

    // After the upload is done, update the state
    if (selectedFolder.length > 0) {
      setSelectedFolder(old =>
        old.map(item =>
          item.id === selectedFolder[0].id
            ? {
                ...item,
                files: (item as FolderType).files + newAttachments.length,
                content: [...selectedFolder[0]?.content, ...newAttachments],
              }
            : item
        )
      )
    }

    setAttachments(old => {
      if (selectedFolder.length > 0) {
        return old.map(item => {
          return item.id === selectedFolder[0].id
            ? {
                ...item,
                files: (item as FolderType).files + newAttachments.length,
                content: [...selectedFolder[0]?.content, ...newAttachments],
              }
            : item
        })
      }
      return [...old, ...newAttachments]
    })

    // Clear the input
    e.target.value = ''
  } catch (error) {
    console.log(error)
    toast.error('Upload failed. Please try again.')
  }
}

export function formatTime(seconds: number) {
  const days = Math.floor(seconds / (24 * 3600))
  seconds %= 24 * 3600
  const hours = Math.floor(seconds / 3600)
  seconds %= 3600
  const minutes = Math.floor(seconds / 60)
  seconds = Math.floor(seconds % 60)

  if (days > 0) return `${days}d `
  if (hours > 0) return `${hours}h `
  if (minutes > 0) return `${minutes}m `
  return `${seconds}s`
}

export const getFileType = (file: Blob | null): FileType => {
  if (!file) return FileType.Unknown
  if (file.type.startsWith('audio/')) return FileType.Audio
  if (file.type.startsWith('text/')) return FileType.Text
  if (file.type.startsWith('image/')) return FileType.Image
  if (file.type.startsWith('video/')) return FileType.Video
  if (file.type.startsWith('application/pdf')) return FileType.Pdf
  return FileType.Unknown
}

