        children
      ) : (
        <AlertDialogSheet
          header={{
            head: 'Upload',
            description: 'upload your attahment here and submit.',
          }}
          actions={{
            continue: () => {
              setAttachments([])
              setAttachmentsState([])
            },
          }}
          footer={{
            submit: {
              children: (
                <Button
                  disabled={attachmentsState.length === 0}
                  className="px-6"
                  onClick={() => {
                    setAttachments(prev => [...prev, ...attachmentsState])
                    setAttachmentsState([])
                  }}
                >
                  Submit
                </Button>
