export interface NotionEditorLinkManagerProps {
  states: ReturnType<typeof useTextmenuStates>
  commands: ReturnType<typeof useTextmenuCommands>
  editor: Editor
}

const MDXEditorLink: React.FC<NotionEditorLinkManagerProps> = ({
  states,
  commands,
  editor,
}: NotionEditorLinkManagerProps) => {
  const [url, setUrl] = React.useState('')

  const openDialog = () => {
    const previousUrl = editor.getAttributes('link').href
    setUrl(previousUrl || '')
  }

  return (
    <DialogWrapper
      trigger={{
        children: (
          <Button
            variant={'ghost'}
            size={'icon'}
            label={{ children: 'Link', showLabel: true, side: 'top' }}
            icon={{ children: Link2 }}
            className={cn('w-[1.85rem] h-[1.85rem]', states.isLink && 'bg-foreground/10')}
            onClick={openDialog}
          />
        ),
      }}
      content={{
        className: 'flex flex-col center w-[400px]',
        children: (
          <>
            <DialogHeader>
              <DialogTitle>Set Link</DialogTitle>
            </DialogHeader>
            <DialogDescription> Set the link here. Click save when you're done.</DialogDescription>
            <div>
              <Input
                type="text"
                value={url}
                onChange={e => setUrl(e.target.value)}
                placeholder="Enter URL"
              />
            </div>
            <div className="flex items-center justify-end gap-2">
              <DialogClose asChild>
                <Button variant="secondary">Cancel</Button>
              </DialogClose>
              <DialogClose asChild>
                <Button
                  onClick={() => {
                    url.length > 0 ? commands.onLink(url, true) : editor.commands.unsetLink()
                  }}
                >
                  Apply
                </Button>
              </DialogClose>
            </div>
          </>
        ),
      }}
    />
  )
}

export type ToolBarToggleButtonsProps = {
  commands: ReturnType<typeof useTextmenuCommands>
  states: ReturnType<typeof useTextmenuStates>
}

const MDXToggleButtons = ({ commands, states }: ToolBarToggleButtonsProps) => {
  return (
    <>
      <div className="flex items-center justify-start gap-1 shrink-0">
        {bubbleMenuIconsData.map((item, idx) => {
          return (
            <Button
              key={idx}
              size={'icon'}
              variant={'ghost'}
              label={{
                children: item.label,
                showLabel: true,
                side: 'top',
              }}
              icon={{ children: item.icon }}
              className={cn('w-[1.85rem] h-[1.85rem]', states[item.value] && 'bg-foreground/10')}
              onClick={commands[item.action] as React.MouseEventHandler<HTMLButtonElement>}
            />
          )
        })}
      </div>
    </>
  )
}

const ColorWeelIcon = ({ className }: { className: string }) => (
  <svg
    viewBox="0 0 202 202"
    fill="none"
    xmlns="http://www.w3.org/2000/svg"
    xmlnsXlink="http://www.w3.org/1999/xlink"
    className={cn('!stroke-none', className)}
  >
    <rect
      width="202"
      height="202"
      fill="url(#pattern0_301_437)"
    />
    <defs>
      <pattern
        id="pattern0_301_437"
        patternContentUnits="objectBoundingBox"
        width="1"
        height="1"
      >
        <use
          xlinkHref="#image0_301_437"
          transform="scale(0.0049505)"
        />
      </pattern>
      <image
        id="image0_301_437"
        width="220"
        height="220"
        xlinkHref="data:image/png;base64,"
      />
    </defs>
  </svg>
)

//NOTE: We memorize the button so each button is not rerendered
// on every editor state change
const MDXToggleButtonsMemo = React.memo(MDXToggleButtons)
const MDXHeadingMemo = React.memo(MDXHeading)
const MDXColorHighlightMemo = React.memo(MDXColorWrapper)
const MDXColorColorMemo = React.memo(MDXColorWrapper)
const MDXLinkMemo = React.memo(MDXEditorLink)
const MDXrAligaMemo = React.memo(MDXEditoerAlign)

export interface MDXTextEditorToolbarProps {
  editor: Editor
}

const MDXTextEditorToolbar = ({ editor }: MDXTextEditorToolbarProps) => {
  const commands = useTextmenuCommands(editor)
  const states = useTextmenuStates(editor)
  const blockOptions = useTextmenuContentTypes(editor)

  const activeItem = React.useMemo(
    () => blockOptions.find(option => option.type === 'option' && (option.isActive!() ?? false)),
    [blockOptions]
  )

  //{
  //  <MemoContentTypePicker options={blockOptions} />
  //  <MemoFontFamilyPicker
  //    onChange={commands.onSetFont}
  //    value={states.currentFont || ''}
  //  />
  //  <MemoFontSizePicker
  //    onChange={commands.onSetFontSize}
  //    value={states.currentSize || ''}
  //  />
  //}
  return (
    <BubbleMenu
      editor={editor}
      tippyOptions={{ duration: 100 }}
    >
      <div className="flex items-center gap-[.325rem] p-1.5 border border-border border-solid ronded-[.5rem] bg-background rounded-lg fixed">
        <MDXrAligaMemo
          commands={commands}
          states={states}
        />
        <MDXHeadingMemo
          trigger={<Heading />}
          activeItem={activeItem?.label || ''}
          commands={commands}
        />
        <Separator
          orientation="vertical"
          className="h-[26px]"
        />
        <MDXToggleButtonsMemo
          commands={commands}
          states={states}
        />
        <Separator
          orientation="vertical"
          className="h-[26px]"
        />
        <MDXLinkMemo
          editor={editor}
          commands={commands}
          states={states}
        />
        <MDXColorHighlightMemo
          trigger={{
            trigger: Highlighter,
            label: 'Highlight',
          }}
          currentColor={states.currentHighlight}
          commands={{
            onChangeColor: commands.onChangeHighlight,
            onClearColor: commands.onClearHighlight,
          }}
        />
        <MDXColorColorMemo
          trigger={{
            trigger: Pencil,
            label: 'Color',
          }}
          currentColor={states.currentColor}
          commands={{
            onChangeColor: commands.onChangeColor,
            onClearColor: commands.onClearColor,
          }}
        />
      </div>
    </BubbleMenu>
  )
}

export { MDXTextEditorToolbar }
